{
    "query": "query FilterArticles($title: String!, $stage: String!, $journal: String!) { articles(where: { _or: [ { title: { _ilike: $title } }, { stage: { _eq: $stage } }, { journal: { name: { _ilike: $journal } } } ] }) { id title stage journal { name } } }",
